# Аргументы командной строкиimport argparseimport socketclass MorseClient(object):    def __init__(self, ip, port):        self.__address = (ip, port)    def connect(self):        while 1:            msg = input('Введите сообщение: ')            if msg:                with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:                    s.connect(self.__address)                    s.send(msg.encode('utf-8'))                    data = s.recv(8192)  # 8Kb                    answer = data.decode('utf-8')                    print('Ответ: {}\n'.format(answer))if __name__ == '__main__':    parser = argparse.ArgumentParser(description='Клиент для сервера Морзе')    parser.add_argument('-i',                        '--ip',                        help='IP адрес сервера',                        default='127.0.0.1')    parser.add_argument('-p',                        '--port',                        help='Порт сервера',                        default=6666,                        type=int)    arguments = parser.parse_args()    client = MorseClient(arguments.ip, arguments.port)    client.connect()