# configparserimport osfrom configparser import ConfigParserclass Config(ConfigParser):    def __init__(self, filename, *args, **kwargs):        super().__init__(*args, **kwargs)        self.__filename = filename        self.load()    def __del__(self):        """Деструктор"""        # self.save()    def load(self):        if os.path.exists(self.__filename):            self.read([self.__filename])    def save(self):        with open(self.__filename, 'w') as f:            self.write(f)    @property    def ip(self):        return self.get(self.default_section, 'ip', fallback='127.0.0.1')    @property    def port(self):        return self.get(self.default_section, 'port', fallback=6666)